mapper.select:
		select
        fhk.uuid, fhk.yjsm, fhk.xgwj, fhk.sqr, fhk.sqrbm, fhk.sqsj, fhk.zzchysj, fhk.zzchynr, fhk.ldxzhysj,
        fhk.ldxzhynr, fhk.ydrs, fhk.sdrs, fhk.rymd, fhk.cgd, fhk.gs, fhk.hccl,
        fhk.act_title, fhk.pid, fhk.remark, fhk.doc_state, fhk.flow_sn, fhk.flow_state, fhk.inserter,
        fhk.insert_time, fhk.updater, fhk.update_time, b.name as flow_state_name
		from flow_hr_kjldgbxb fhk left join t_dictionary b on fhk.flow_state = b.code and b.type = 'flowState' where 1=1
        
            and fhk.inserter = #{currentUserId}
        
        
            AND( fhk.flow_state=#{flowState}
            
                or fhk.flow_state=6 or fhk.flow_state=7
            
            
                or fhk.flow_state=1
            
            
                or fhk.flow_state=4
            
            )
        
        
            and (fhk.act_title like concat('%',#{keyWord},'%'))
        
        
            and (fhk.sqr like concat('%',#{sqr},'%'))
        
        
            and DATE_FORMAT(fhk.insert_time,'%Y-%m-%d') >= #{startTime}
        
        
            and DATE_FORMAT(fhk.insert_time,'%Y-%m-%d') <= #{endTime}
        
        
            order by fhk.insert_time desc
        
        
            order by ${field} ${order}
